$(function(){$(".btn-init-db").click(function(){var url=$(this).data('url');var surl=$(this).data('surl');$.ajax({type:"POST",url:url,data:null,contentType:"application/json; charset=utf-8",success:function(){$(location).attr('href',surl);alert("Database Initialization is done.")},error:function(){alert("Database Initialization failed.");},});});$(".btn-init-admin").click(function(){var nickname=$("#inputAdminname").val();var password=$("#inputAdminpass").val();var verify_password=$("#verifyAdminpass").val();var url=$(this).data("url");var surl=$(this).data("surl");var data={'nickname':nickname,'password':password};if(password!==verify_password){alert("Input passwords are not consistent.");}else{console.log("Input passwords are consistent.");$.ajax({type:"POST",url:url,data:JSON.stringify(data),contentType:"application/json; charset=utf-8",success:function(){$(location).attr('href',surl);alert("Admin Account Initialization is done.")},error:function(){alert("Admin Account Initialization failed.");},});}});$(".btn-update-admin-password").click(function(){var nickname=$("#verifyAdminname").val();var current_password=$("#verifyCurrentAdminpass").val();var new_password=$("#inputNewAdminpass").val();var verify_new_password=$("#verifyNewAdminpass").val();var url=$(this).data("url");var surl=$(this).data("surl");var data={'nickname':nickname,'current_password':current_password,'new_password':new_password};if(new_password!==verify_new_password){alert("Input new passwords are not consistent.");}else{console.log("Input new passwords are consistent.");$.ajax({type:"PUT",url:url,data:JSON.stringify(data),contentType:"application/json; charset=utf-8",success:function(){$(location).attr('href',surl);alert("Admin password is updated.")},error:function(){alert("Failed to udpate admin password.");},});}});});